import axios from 'axios'

const API_KEY = '2b9c6bef29017ea25df8f3994053dd38'

const config = {
    headers: {
        'Content-Type': 'application/json',
        'Access-Control-Allow-Origin': '*'
    }
}

const api = axios.create({
    baseURL: 'https://api.themoviedb.org/3',
    config: config
})

const fetch = async(params) =>{
    const resp = await api.get(params);
    // console.log(resp.data.results)
    return resp.data;
}

export default [
            {
                slug: 'originals',
                title: 'Originais do Netflix',
                items : fetch(`/discover/tv?with_network=213&language=pt-BR&api_key=${API_KEY}`)
            },
            {
                slug: 'trending',
                title: 'Recomendados para Você',
                items : fetch(`/trending/all/week?with_network=213&language=pt-BR&api_key=${API_KEY}`)
            }
        ];
