import React from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faChevronLeft, faChevronRight } from '@fortawesome/free-solid-svg-icons'
import './style.css'


const MovieRow = ({list}) => {

    const rollLeft = (event) => {
        console.log(list)
        let row = document.getElementsByClassName('list');
        let actualPos = row[0].scrollLeft;
        // let scrollTimer = setInterval(()=>{
        //     if(row[0].scrollLeft > actualPos - 1020 && row[0].scrollLeft > 30) {
        //         row[0].scrollLeft -= 70
        //     } else {
        //         console.log('Parou')
        //         clearInterval(scrollTimer)
        //     }
        // console.log()
        // }, 24)
    }

    const rollRight = (event) => {
        let row = document.getElementById(list.slug);
        let actualPos = row[0].scrollLeft;
        let width = row[0].getBoundingClientRect().width
        let listWidth = row[0].childElementCount * 170;
        console.log(listWidth)
        let scrollTimer = setInterval(()=>{
            if(row[0].scrollLeft < actualPos + 1020 && row[0].scrollLeft + width < listWidth) {
                console.log(row[0].scrollLeft)
                row[0].scrollLeft += 70
            } else {
                console.log('Parou')
                clearInterval(scrollTimer)
            }

        }, 24)

    }

    return (
        <div className='row--header'>
            <span>{list.title}</span>

            <div className='row--imgs'>
                <div className='left--arrow' onClick={(e) => rollLeft(e.target)}> <FontAwesomeIcon icon={faChevronLeft} /> </div>
                <div className='right--arrow' onClick={(e) => rollRight(e.target)}> <FontAwesomeIcon icon={faChevronRight} /></div>
                <div className='list' id={list.slug} style={{

                }}>
                    {list.items.results.map((movie, key) =>
                        <div key={key} className='movie--poster'>
                            <img src={`https://image.tmdb.org/t/p/w300${movie.poster_path}`} />
                        </div>)
                    }
                </div>
            </div>
        </div>
    )
}

export default MovieRow;
