import React, { useState, useEffect } from 'react'
import Tmdb from '../services/Tmdb'
import Header from '../components/Header/index'
import './App.css'

export default function App() {

    const [movieList, setMovieList] = useState([]);
    const [background, setBackground] = useState(true);
    const [featuredMovie, setFeaturedMovie] = useState(null);

    useEffect(() => {
        async function loadAll() {
            let list = await Tmdb.getHomeList();
            let originals = movieList[0].items.results
            console.log(originals)

            setMovieList(list);
        }
        loadAll();
    }, []);

    window.addEventListener('scroll', () => {
        if(window.scrollY > 50) {
            setBackground(false)
        } else {
            setBackground(true)
        }
    })

    return (
        <div id='netflix'>
            <Header background={background}/>


            <div className='row--header'>

                {movieList.map(list =>
                    <div id={list.slug} key={list.slug}>

                        <span>{list.title}</span>

                        <div className='row--imgs' >
                            {list.items.results.map(movie => <img key={movie.id} className='movie--poster' src={`https://image.tmdb.org/t/p/w300${movie.poster_path}`} ></img>)}
                        </div>

                    </div>
                )}

            </div>

        </div>
    )
}
