import React from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faChevronLeft, faChevronRight } from '@fortawesome/free-solid-svg-icons'
import './style.css'


const MovieRow = (props) => {

    let move = 0;

    const rollRight = (event) => {
        let imgList = event.parentNode;
        imgList.style.setProperty('right', 1020 + 'px');
    }

    const rollLeft = (node) => {
        let row = document.getElementsByClassName('row--imgs')
        row[0].style.marginLeft = move + 'px';
    }

    return (
        <div className='row--header'>
            <span>{props.list.title}</span>

            <div className='row--imgs'>
                <div className='left--arrow' onClick={(e) => rollRight(e.target)}> <FontAwesomeIcon icon={faChevronLeft} /> </div>
                <div className='right--arrow'> <FontAwesomeIcon icon={faChevronRight} /></div>
                <div className='list'>
                {props.list.items.results.map((movie, key) =>
                    <div key={key} className='movie--poster'>
                        <img src={`https://image.tmdb.org/t/p/w300${movie.poster_path}`} />
                    </div>)
                }
                </div>
            </div>
        </div>
    )
}

export default MovieRow;
