import { useEffect } from 'react'
import './style.css'

const FeaturedMovie = (props) => {

    return (
        <div className='featured--container'>
            <img className='featured--img' src={`https://image.tmdb.org/t/p/original${props.movie.backdrop_path}`} />
            {/* <h3 className='featured--avg'></h3> */}
            <div className='featured--info'>
                <h3 className='featured--info avg'>{`${props.movie.vote_average} ponto${props.movie.vote_average <=1 ? '': 's'}`}</h3>
                <h3>{`${props.movie.firs_air_date[3]}`}</h3>
            </div>
            <h3 className='featured--overview'>{`${props.movie.overview}`}</h3>
        </div>
    )
}

export default FeaturedMovie
