import React from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faChevronLeft, faChevronRight } from '@fortawesome/free-solid-svg-icons'
import './style.css'


const MovieRow = (props) => {

    let move = 0;

    const rollRight = () => {
        let row = document.getElementsByClassName('row--imgs')
        move -= (170 * 6)
        row[0].style.marginRight = move + 'px';
    }

    const rollLeft = () => {
        let row = document.getElementsByClassName('row--imgs')
        move += 170 * 6
        // if (row[0].style.marginLeft.substring(0, 2) < 40) return;
        row[0].style.marginLeft = move + 'px';
    }

    return (
        <div className='row--header'>
            <span>{props.list.title}</span>
            <div className='list'>
                <div className='left--arrow' onClick={rollLeft}> <FontAwesomeIcon icon={faChevronLeft} /> </div>
                <div className='right--arrow' onClick={rollRight}> <FontAwesomeIcon icon={faChevronRight} /></div>
                <div className='row--imgs'>
                    {props.list.items.results.map((movie, key) =>
                        <div key={key} className='movie--poster'>
                            <img src={`https://image.tmdb.org/t/p/w300${movie.poster_path}`} />
                        </div>)
                    }
                </div>
            </div>
        </div>
    )
}

export default MovieRow;
