import React, {setState, useEffect, useState} from 'react'
import './style.css'

const Header = () => {

    const [background, setBackground] = useState(true);   

    function changeBackground() {
        if(window.scrollY < 10) {
            setBackground(true)
        } else {
            setBackground(false)
        }
    }

    window.addEventListener('scroll', changeBackground)

    return(
        <div className={background ? 'header transparent': 'header normal'}>
            <div className='menu left'>
                <img id='logo--img' src='https://logodownload.org/wp-content/uploads/2014/10/netflix-logo-1-1.png'></img>
                <ul>
                    <li id='start'><a>Início</a></li>
                    <li><a>Séries</a></li>
                    <li><a>Filmes</a></li>
                    <li><a>Bombando</a></li>
                    <li><a>Minha lista</a></li>
                </ul>
            </div>
            <div className='menu right'>
                <div className='search--container'>
                    <FontAwesomeIcon icon="fa-solid fa-magnifying-glass" />
                    <input className='search--box' type='text'></input>
                </div>
                <span>INFANTIL</span>
                <img id='profile--img' src='https://upload.wikimedia.org/wikipedia/commons/0/0b/Netflix-avatar.png'></img>
            </div>
        </div>
    )
}

export default Header
