import React, { useState, useEffect } from 'react'
import Tmdb from '../services/Tmdb'
import Header from '../components/Header/index'
import MovieRow from '../components/MovieRow/index'
import './App.css'

export default function App() {

    const [movieList, setMovieList] = useState(false);
    const [background, setBackground] = useState(true);
    const [featuredMovie, setFeaturedMovie] = useState(NULL);

    useEffect(() => {
        async function loadAll() {
            let list = await Tmdb.getHomeList();
            let originals = await list[0].items.results
            let featured = await Math.floor(Math.random() * (originals.length + 1));
            setFeaturedMovie(originals[featured])
            setMovieList(list);
        }
        loadAll();
    }, []);

    window.addEventListener('scroll', () => {
        if (window.scrollY > 50) {
            setBackground(false)
        } else {
            setBackground(true)
        }
    })

    return (
        <div id='netflix'>
            <Header background={background} />

            <div>
                {featuredMovie &&
                    <img src={`https://image.tmdb.org/t/p/w300${featuredMovie.poster_path}`} ></img>}
            </div>

            {movieList &&
                movieList.map(list => {
                    <MovieRow list={list}/>
                })
            } 

        </div>
    )
}
